mapper.sql:
WITH r1 AS
    (
        SELECT sheetID,carSubmit,sheetSS,CarNo,carSubmitTel
        FROM tsheet
        WHERE sheetSS = #{uuserID}
          AND date BETWEEN ${startDate} AND ${endDate}
    ),
    r2 AS
   (
SELECT *
FROM tsheet_sub
    ),
    r3 AS
    (
SELECT *
FROM r1
    INNER JOIN r2 ON r1.sheetID = r2.sheetID2
    ),
    tdrive_table AS
    (
SELECT *
FROM tdrive
WHERE drvDate BETWEEN ${startDate} AND ${endDate}
  AND driveSS = #{uuserID}
    )
