mapper.select:
select * from tSheet
where
date = #{date}
and
carSubmitTel = #{carSubmitTel}
and
sheetSS=#{sheetSS}
order by carSubmit asc

select sheetID, carSubmit, salesman, carSubmitTel from tSheet
where
carSubmit like CONCAT('%', #{carSubmit}, '%')
or
carSubmitTel = #{carSubmitTel}
or
salesman = #{salesman}
order by carSubmit asc;

mapper.update:
update tSheet
set
sheetSS2=#{sheetSS2}
where
sheetSS=#{sheetSS}
and
sheetID=#{sheetID}

mapper.delete:
delete from tSheet
where carSubmitTel = #{carSubmitTel}
and date = #{date}
and chk1 = 0
and writerIDX = #{writerIDX}
