mapper.select:
select * from tSheet
where date=#{date} and carSubmit=#{carSubmit} and carSubmitTel=#{carSubmitTel} and salesman=#{salesman}
and sheetSS=#{sheetSS}
order by carSubmit asc

select sheetID, DATE_FORMAT(date, '%Y-%m-%d') as date, carSubmit, salesman, carSubmitTel from tSheet
where
carSubmit like CONCAT('%', #{carSubmit}, '%')
or
carSubmitTel = #{carSubmitTel}
or
salesman = #{salesman}
order by carSubmit asc;

mapper.update:
update tSheet


        carSubmit=#{carSubmit},


        carSubmitTel=#{carSubmitTel},


        salesman=#{salesman}


where carSubmitTel=#{carSubmitTel}
and (carSubmit != carSubmit or carSubmitTel != carSubmitTel or salesman != salesman)
